import React from 'react';
import './ToDoList.css';

class ToDoList extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            newItem: "",
            counter: 4, 
            listArr: []
        }
        this.addItem=this.addItem.bind(this);
        this.chekedItem= this.chekedItem.bind(this);

        this.state.listArr.push({index: 1, value:"learn react", done: false})
        this.state.listArr.push({ index: 2, value: "quit my job", done: true })
        this.state.listArr.push({ index: 3, value: "cook lunch", done: false })

    }
      addItem(event){
          const newValue = document.getElementById("newItemValue").value;
        const addedItem= {index: this.state.counter, value: newValue ,done: false};
        this.setState({listArr: this.state.listArr.concat(addedItem), counter: this.state.counter+1})
      }


        render() {
            const fullList= [];
            for(let i=0; i<this.state.listArr.length; i++){
               const listContent=(
                   <li>   <input type="checkbox" onChange={this.chekedItem}  /> {this.state.listArr[i].value} </li>
               )
                fullList.push(listContent);
            }


            return (
                <div>
                    <h1>To Do List</h1>
                        <input id="newItemValue" placeholder="Enter task">
                        </input>
                        <button  onClick={this.addItem}>Add</button>
                        <ul>
                            {fullList}
                        </ul>
                </div>
            );
        }


}



export default ToDoList;
