import React from 'react';


class ToDoList extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            newItem: "",
            counter: 0, 
            listArr: []
        }
        this.addItem=this.addItem.bind(this);
        this.state.listArr.push({index: 1, value:"learn react", done: false})
        this.state.listArr.push({ index: 2, value: "quit my job", done: true })
        this.state.listArr.push({ index: 3, value: "cook lunch", done: false })

    }
      addItem(event){
        const addedItem= event.target.value;

        this.setState({newItem: addedItem})
      }
        render() {
            const fullList= [];
            for(let i=0; i<this.state.listArr.length; i++){
               const listContent=(
                   <li>   <input type="checkbox"  /> {this.state.listArr[i].value} </li>
               )
                fullList.push(listContent);
            }


            return (
                <div>
                    <h1>To Do List</h1>
                    <form >
                        <input value={this.state.newItem}  placeholder="Enter task">
                        </input>
                        <button  onClick={this.addItem}>Add</button>
                        <ul>
                            {fullList}
                        </ul>
                    </form>
                </div>
            );
        }


}



export default ToDoList;
