import React from 'react';


class ToDoList extends React.Component {

    constructor(props) {
        super(props);
        this.state = {

            newItem: "",
            counter: 0, 
            listArr: []
        }

        this.listArr.push({index: 1, value:"learn react", done: false})
        this.listArr.push({ index: 2, value: "quit my job", done: true })
        this.listArr.push({ index: 3, value: "cook lunch", done: false })

        this.addItem = this.addItem.bind(this);
    }

        render() {

            return (
                <div>
                    <h1>To Do List</h1>
                    <form >
                        <input value={this.state.newItem}  placeholder="Enter task">
                        </input>
                        <button onClick={this.addItem} type="submit">Add</button>
                    </form>
                </div>
            );
        }


}



export default ToDoList;
